// Получаем текущее и предыдущее значения temp1
const currentTemp1 = global.get('temp1');
const lastTemp1 = global.get('lastTemp1') || currentTemp1;
const currentTime = Date.now();
const lastUpdateTime = global.get('lastUpdateTime') || 0;
const threshold = 60000; // Пороговое время в миллисекундах (например, 60 секунд)
const temp1ChangeThreshold = 0.1; // Порог изменения temp1 (например, 0.1 единицы)

// Проверяем, изменилось ли значение temp1
if (Math.abs(currentTemp1 - lastTemp1) < temp1ChangeThreshold) {
    // Если значение temp1 не изменилось больше, чем на temp1ChangeThreshold, проверяем время
    if (currentTime - lastUpdateTime > threshold) {
        // Если данные не обновлялись дольше порогового времени, выводим предупреждение
        node.status('Возможно, потеряно соединение с оборудованием');
    }
} else {
    // Если значение temp1 изменилось, обновляем lastTemp1 и lastUpdateTime
    global.set('lastTemp1', currentTemp1);
    global.set('lastUpdateTime', currentTime);
    node.status('ok');
}

