async function assemblyProcess(assemblyCode, startPressed, pausePressed, resetPressed) {
    // Assume assemblyCode is provided as an argument
    while (!resetPressed) {
        // Check if pause button is pressed
         if (startPressed && !pausePressed) {
        switch (assemblyCode) {
            case 124:
                await step1();
                await step2();
                await step3();
                await step4();
                await step5();
                await step6();
                await step7();
                await step8();
                await moveToParking();
                break;
            // Add more cases for different assembly codes if needed
        }
    }   // Check if pause button is pressed
    if (pausePressed) {
        await pause(); // Call the pause function
        pausePressed = 0; // Reset pause flag
    }

    // Check if reset button is pressed
    if (resetPressed) {
        break; // Exit the loop
    }

    // Check if all steps are completed
    if (!resetPressed) {
        console.log("Assembly completed.");
        break; // Exit the loop
    }

    // Reset global variables after assembly is complete or reset is pressed
    startPressed = 0;
    pausePressed = 0;
    resetPressed = 0;
    global.set("startPressed", 0);
    global.set("stopPressed", 0);
    global.set("resetPressed", 0);
    }
}


