async function executeAssembly(tochki, assemblyPoints) {
    // Assembly points for Robot 2

    for (let i = 0; i < assemblyPoints.length; i++) {
        const pointIndex = assemblyPoints[i];
        const point = tochki[pointIndex];
        move(point);
        await sleep(4000);

        // Gripper actions for specific points
        if (i % 2 === 0) { // Even points
            actGripper(); // Go down
            await sleep(2000);
            actGripper(); // Close gripper
            await sleep(2000);
            actGripper(); // Go up
            await sleep(2000);
        } else { // Odd points
            actGripper(); // Go down
            await sleep(2000);
            actVacuum(); // Open gripper
            await sleep(2000);
            actGripper(); // Go up
            await sleep(2000);
        }
    }

    // Return to parking position
    move(tochki[14]);
    await sleep(4000);
}

